mapper.select:
   SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
   SELECT  B.*
   FROM    BOARD B
   LEFT JOIN BOARDLIKE BL ON B.B_IDX = BL.B_IDX
   WHERE B.M_NO = #{m_no}
     ORDER BY B_WRITEDATE DESC
    ) A
)
WHERE RN BETWEEN #{start} AND #{end}

   SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
   SELECT  *
   FROM    KIDZZONE 
     ORDER BY KZ_NO
    ) A
)
WHERE RN BETWEEN #{start} AND #{end}


SELECT  M_NAME
FROM    MENU
WHERE   M_NO = #{m_no}
