mapper.select:

SELECT  M_NAME
FROM    MENU
WHERE   M_NO = #{m_no}

   SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
   SELECT  *
   FROM    BOARD
   WHERE   M_NO = #{m_no}
     ORDER BY B_WRITEDATE DESC
    ) A
)
WHERE RN BETWEEN #{pg.start} AND #{pg.end}

   SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
   SELECT  *
   FROM    BOARD
   WHERE   M_NO = #{m_no}

        AND UPPER(${searchOption}) LIKE '%' || UPPER(#{searchKeyword}) || '%'

      ORDER BY B_WRITEDATE DESC
    ) A
)
WHERE RN BETWEEN #{pg.start} AND #{pg.end}


SELECT  COUNT(*)
FROM    BOARD
WHERE   M_NO = #{m_no}

mapper.update:
UPDATE BOARD SET 
B_READCOUNT = (B_READCOUNT+1)
WHERE B_IDX = #{b_idx}

mapper.select:

 SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
SELECT *
    FROM COMMT
WHERE B_IDX = ${b_idx}
ORDERBY C_IDX DESC
    ) A
)
WHERE RN BETWEEN #{start} AND #{end}
