mapper.select:
   SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
   SELECT  *
   FROM    BOARD
   WHERE   M_NO = #{m_no}
    ) A
)
WHERE RN BETWEEN #{pg.start} AND #{pg.end}


SELECT  COUNT(*)
FROM    BOARD
WHERE   M_NO = #{m_no}


SELECT  M_NAME
FROM    MENU
WHERE   M_NO = #{m_no}

mapper.update:
UPDATE BOARDBOOT SET 
HIT = (HIT+1)
WHERE BNO = #{bno}

UPDATE BOARDBOOT SET 
TITLE = #{title},
WRITER = #{writer},
CONTENT = #{content}
WHERE BNO = #{bno}
