mapper.select:

SELECT  M_NAME
FROM    MENU
WHERE   M_NO = #{m_no}

   SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
   SELECT  *
   FROM    BOARD B LEFT JOIN PUSER U ON B.U_NO = U.U_NO
   WHERE   M_NO = #{m_no}
     ORDER BY B_WRITEDATE DESC
    ) A
)
WHERE RN BETWEEN #{pg.start} AND #{pg.end}

   SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
   SELECT  *
   FROM    BOARD B LEFT JOIN PUSER U ON B.U_NO = U.U_NO
   WHERE   M_NO = #{m_no}

        AND UPPER(${searchOption}) LIKE '%' || UPPER(#{searchKeyword}) || '%'

      ORDER BY B_WRITEDATE DESC
    ) A
)
WHERE RN BETWEEN #{pg.start} AND #{pg.end}


SELECT  COUNT(*)
FROM    BOARD
WHERE   M_NO = #{m_no}


SELECT  *
FROM    BOARD B LEFT JOIN PUSER U ON B.U_NO = U.U_NO
WHERE   B.B_IDX = #{b_idx}


SELECT  COUNT(*)
FROM    BOARDLIKE
WHERE   B_IDX = #{b_idx} 
AND     U_NO  = #{u_no}

mapper.update:
UPDATE BOARD SET 
B_READCOUNT = (B_READCOUNT+1)
WHERE B_IDX = #{b_idx}

mapper.select:

   SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
SELECT *
    FROM COMMT C LEFT JOIN PUSER U ON C.U_NO = U.U_NO
WHERE B_IDX = ${b_idx}
ORDER BY C_IDX DESC
    ) A
)
WHERE RN BETWEEN #{pg.start} AND #{pg.end}
