mapper.select:

 SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
SELECT *
    FROM KIDZZONE

    ) A
)
WHERE RN BETWEEN #{start} AND #{end}


 SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
SELECT *
    FROM KIDZZONE
    ) A
)
WHERE RN BETWEEN #{pg.start} AND #{pg.end}


 SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
SELECT *
    FROM KIDZZONE
    WHERE 1 = 1

        AND UPPER(${searchOption}) LIKE '%' || UPPER(#{searchKeyword}) || '%'


        AND KZ_ADDRESS LIKE '%' || #{kz_location} || '%'


        AND KZ_CATEGORY LIKE #{kz_category}

    ) A
)
WHERE RN BETWEEN #{pg.start} AND #{pg.end}
