mapper.select:

 SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
SELECT *
    FROM KIDZZONE

    ) A
)
WHERE RN BETWEEN #{start} AND #{end}


 SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
SELECT *
    FROM KIDZZONE
    ) A
)
WHERE RN BETWEEN #{pg.start} AND #{pg.end}


 SELECT *
FROM (
    SELECT ROWNUM RN, A.*
    FROM (
SELECT *
    FROM KIDZZONE
    WHERE 1 = 1

        AND UPPER(${searchOption}) LIKE '%' || UPPER(#{searchKeyword}) || '%'


        AND KZ_ADDRESS LIKE '%' || #{kz_location} || '%'


        AND KZ_CATEGORY LIKE #{kz_category}

    ) A
)
WHERE RN BETWEEN #{pg.start} AND #{pg.end}

mapper.delete:

DELETE FROM ZONEREVIEW
WHERE R_NO  =  #{r_no}
AND   U_NO  =  #{u_no}

mapper.select:

SELECT KZ_NAME
FROM   KIDZZONE
WHERE KZ_NO  =  #{kz_no}

mapper.insert:

INSERT INTO ZONELIKE (
	KZ_NO,
	U_NO
) VALUES (
	#{kz_no},
	#{u_no}
)

mapper.delete:
DELETE FROM ZONELIKE
WHERE KZ_NO = #{kz_no}
AND   U_NO  = #{u_no}

mapper.select:
SELECT COUNT(*)
FROM   ZONELIKE
WHERE  U_NO  =  #{u_no}
