mapper.insert:
INSERT INTO PUSER (
     U_NO
    ,U_ID
    ,U_PW
    ,U_NAME
    ,U_POSTCODE
    ,U_ADDRESS
    ,U_DETAILADDRESS
    ,U_EXTRAADDRESS
    ,U_NICKNAME
    ,U_PHONE
    ,U_PROFILEIMG
    ,U_EMAIL
    ,U_LOGINCOUNT
    ,U_GRADE
) VALUES (
    ( SELECT NVL(MAX(U_NO),0)+1 FROM PUSER)
    ,#{u_id}
    ,#{u_pw}
    ,#{u_name}
    ,#{u_postcode}
    ,#{u_address}
    ,#{u_detailaddress}
    ,#{u_extraaddress}
    ,#{u_nickname}
    ,#{u_phone}
    , NULL
    ,#{u_email}
    ,0
    ,'A'
)

